import { DefaultAuthentication } from "typeorm/driver/sqlserver/authentication/DefaultAuthentication.js";
import { AzureActiveDirectoryAccessTokenAuthentication } from "typeorm/driver/sqlserver/authentication/AzureActiveDirectoryAccessTokenAuthentication.js";
import { AzureActiveDirectoryDefaultAuthentication } from "typeorm/driver/sqlserver/authentication/AzureActiveDirectoryDefaultAuthentication.js";
import { AzureActiveDirectoryMsiAppServiceAuthentication } from "typeorm/driver/sqlserver/authentication/AzureActiveDirectoryMsiAppServiceAuthentication.js";
import { AzureActiveDirectoryMsiVmAuthentication } from "typeorm/driver/sqlserver/authentication/AzureActiveDirectoryMsiVmAuthentication.js";
import { AzureActiveDirectoryPasswordAuthentication } from "typeorm/driver/sqlserver/authentication/AzureActiveDirectoryPasswordAuthentication.js";
import { AzureActiveDirectoryServicePrincipalSecret } from "typeorm/driver/sqlserver/authentication/AzureActiveDirectoryServicePrincipalSecret.js";
import { NtlmAuthentication } from "typeorm/driver/sqlserver/authentication/NtlmAuthentication.js";
export type SqlServerConnectionCredentialsAuthenticationOptions = DefaultAuthentication | NtlmAuthentication | AzureActiveDirectoryAccessTokenAuthentication | AzureActiveDirectoryDefaultAuthentication | AzureActiveDirectoryMsiAppServiceAuthentication | AzureActiveDirectoryMsiVmAuthentication | AzureActiveDirectoryPasswordAuthentication | AzureActiveDirectoryServicePrincipalSecret;
/**
 * SqlServer specific connection credential options.
 */
export interface SqlServerConnectionCredentialsOptions {
    /**
     * Connection url where the connection is performed.
     */
    readonly url?: string;
    /**
     * Database host.
     */
    readonly host?: string;
    /**
     * Database host port.
     */
    readonly port?: number;
    /**
     * Database name to connect to.
     */
    readonly database?: string;
    /**
     * Database username.
     */
    readonly username?: string;
    /**
     * Database password.
     */
    readonly password?: string;
    /**
     * Authentication settings
     * It overrides username and password, when passed.
     */
    readonly authentication?: SqlServerConnectionCredentialsAuthenticationOptions;
    /**
     * Once you set domain, driver will connect to SQL Server using domain login.
     * @see SqlServerConnectionCredentialsOptions.authentication
     * @see NtlmAuthentication
     * @deprecated
     */
    readonly domain?: string;
}
